import React, { Component } from 'react';

import { withFirebase } from '../Firebase';
import * as ROUTES from '../../constants/routes';

const MakeStudentPage = () => (
    <div>
      <h1>New Student's info</h1>
      <MakeStudentForm />
    </div>
  );
  
  const INITIAL_STATE = {
    studentName: '',
    sid: 0,
    error: null,
  };
  
  class MakeStudentFormBase extends Component {
    constructor(props) {
      super(props);
  
      this.state = { ...INITIAL_STATE };
    }
  
    onSubmit = event => {
      const { studentName, sid } = this.state;
  
      this.props.firebase
        .student(sid)
        .set({
            sid,
            studentName
        }).then(() => {
            this.setState({ ...INITIAL_STATE });
            this.props.history.push(ROUTES.HOME);
        }).catch(error => {
            this.setState({ error });
        });
  
      event.preventDefault();
    };
  
    onChange = event => {
      this.setState({ [event.target.name]: event.target.value });
    };
  
    render() {
      const {
        studentName,
        sid,
        error
      } = this.state;
  
      const isInvalid =
        sid === 0 ||
        studentName === '';
  
      return (
        <form onSubmit={this.onSubmit}>
          <input
            name="studenName"
            value={studentName}
            onChange={this.onChange}
            type="text"
            placeholder="Full Student Name"
          />
          <input
            name="sid"
            value={sid}
            onChange={this.onChange}
            type="number"
            placeholder="Student's ID"
          />
          <button disabled={isInvalid} type="submit">
            New Student
          </button>
  
          {error && <p>{error.message}</p>}
        </form>
      );
    }
  }
  
  const MakeStudentForm = withRouter(withFirebase(MakeStudentFormBase));
  
  export default MakeStudentPage;
  
  export { MakeStudentForm };
